import React from 'react';
import { Routes, Route } from 'react-router-dom';
import MainPage from './MainPage'; // Assuming you have a component named 'MainPage'

function App() {
  const [activeModal, setActiveModal] = React.useState("");
  const [isLoggedIn, setIsLoggedIn] = React.useState(true);
  const [currentUser, setCurrentUser] = React.useState({});
  const [token, setToken] = React.useState("");
  const [isLoading, setIsLoading] = React.useState(false);
  const [activeSearch, setActiveSearch] = React.useState(false);
  const [newsCards, setNewsCards] = React.useState([]);
  const [isSearchLoading, setIsSearchLoading] = React.useState(true);
  const [errorMessage, setErrorMessage] = React.useState("");
  const [keyword, setKeyword] = React.useState("");
  const [savedCards, setSavedCards] = React.useState([]);

  // Place your component logic, functions, and JSX code here
  // ...

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <ActivePopupContext.Provider value={activeModal}>
        <SavedCardsContext.Provider value={savedCards}>
          <Routes>
            <Route
              exact
              path="/"
              element={
                <MainPage
                  handleSearchSubmit={handleSearchSubmit}
                  handleLoginClick={handleLoginClick}
                  activeSearch={activeSearch}
                  cards={newsCards}
                  isSearchLoading={isSearchLoading}
                  isLoggedIn={isLoggedIn}
                  handleMobileClick={handleMobileClick}
                  handleLogout={handleLogout}
                  handleBook={handleBook}
                  setKeyword={setKeyword}
                  handleSignupClick={handleSignupClick}
                />
              }
            />
            {/* Add more routes and components here as needed */}
          </Routes>
        </SavedCardsContext.Provider>
      </ActivePopupContext.Provider>
    </CurrentUserContext.Provider>
  );
}

export default App;




